$(document).ready(function(){$(".carousel").carousel({full_width:true});$(".countdown").each(function(b,c){var a=new Date("Sat Sep 24 2016 00:00:00 GMT+0100 (BST)");setInterval(function(){var f="";var g=DateDiff.inDays(a);if(g>1){f+=g+" days"}else{if(g==1){f+=g+" day"}}if(f!=""){f+=", "}var d=DateDiff.inHours(a);if(d>1){f+=d+" hours"}else{if(d==1){f+=d+" hours"}}if(d>1){if(f!=""){f+=" & "}}else{if(f!=""){f+=", "}}var e=DateDiff.inMinutes(a);if(e>1){f+=e+" minutes."}else{if(d==1){f+=e+" minutes."}}c.innerHTML=f}.bind(c,a),1000)})});var DateDiff={inDays:function(c){var b=(new Date).getTime();var a=c.getTime();return parseInt((a-b)/(24*3600*1000))},inHours:function(c){var b=(new Date).getTime();var a=c.getTime();return parseInt((a-b)/(3600*1000)%24)},inMinutes:function(c){var b=(new Date).getTime();var a=c.getTime();return parseInt((a-b)/(60*1000)%60)}};