$(document).ready(function(){$(".carousel").carousel({full_width:true});$(".countdown").each(function(b,d){var a=new Date("Sat Sep 24 2016 00:00:00 GMT+0100 (BST)");var c=setInterval(function(){var i=DateDiff.inDays(a);var e=DateDiff.inHours(a);var f=DateDiff.inMinutes(a);var h=DateDiff.inSeconds(a);if(i<0||e<0||f<0||h<0){clearInterval(c);d.innerHTML=""}var g="";if(i>1){g+=i+" days"}else{if(i==1){g+=i+" day"}}if(i>0&&e>0&&f>0){g+=", "}else{if(i>0&&e>0){g+=" & "}}if(e>1){g+=e+" hours"}else{if(e==1){g+=e+" hours"}}if(i>0){g+=" & "}else{if(i==0&&e>0){g+=", "}}if(f>1){g+=f+" minutes"}else{if(f==1){g+=f+" minute"}else{if(i>1||e>1){g+=" 0 minutes"}}}if(i==0){if(e>0||f>0){g+=" & "}if(h==1){g+="1 second"}else{g+=h+" seconds"}}d.innerHTML=g}.bind(d,a),1000)})});var DateDiff={inDays:function(c){var b=(new Date).getTime();var a=c.getTime();return parseInt((a-b)/(24*3600*1000))},inHours:function(c){var b=(new Date).getTime();var a=c.getTime();return parseInt((a-b)/(3600*1000)%24)},inMinutes:function(c){var b=(new Date).getTime();var a=c.getTime();return parseInt((a-b)/(60*1000)%60)},inSeconds:function(c){var b=(new Date).getTime();var a=c.getTime();return parseInt((a-b)/(1000)%60)}};